    @Test
    public void shouldBeConvertFromRandomUserWithOutError() throws IOException {

        List<Customer> converted = getUsersConverter.convertFromRandomUser( randomUser );

        assertThat( converted.size(), is( 5 ));
        assertThat( converted.get( 0 ).getFullName(), is("Oliver Pelletier" ));
        assertThat( converted.get( 0 ).getAge(), is( "29" ));
        assertThat( converted.get( 0 ).getAddress(), is( "9thSt" ));
        assertThat( converted.get( 0 ).getCity(), is( "Sutton" ));
        assertThat( converted.get( 0 ).getState(), is( "Québec" ));

    }
    @Test
    public void shouldNotConvertAndReturnEmptyListFromRandomUser(){
        RuntimeException thrown = Assertions.assertThrows(RuntimeException.class, () -> {
            getUsersConverter.convertFromRandomUser( "" );
        });
    }

