    @Test
    public void shouldSaveDataFromAPIPlaceHoldAtDB(){

        List<Customer> converted = getUsersConverter.convertFromPlaceHolder( placeHolder );

        var fromDb = customerRepository.saveAll( converted );

        assertThat( fromDb.size(), is(converted.size()) );
        assertThat( fromDb.size(), is( 10 ));
        assertNotEquals( fromDb.get( 0 ).getId(), is( null ));

        assertThat( fromDb.get( 0 ).getFullName(), is("LeanneGraham" ));
        assertThat( fromDb.get( 0 ).getAddress(), is( "KulasLight" ));
        assertThat( fromDb.get( 0 ).getCity(), is( "Gwenborough" ));
    }

